CREATE TABLE user( id integer Not Null  AUTO_INCREMENT COMMENT 'automaticky generované id',  name VarChar (100) Not Null   COMMENT 'Jméno uživatele zadané při registraci',  mail VarChar (100) Not Null   COMMENT 'Mail uživatele zadaný při registraci',  pass VarChar (100) Not Null   COMMENT 'Has hesla',  authorized integer Not Null   COMMENT 'Příznak autorizovaného uživatele, 0 - čeká na autorizaci, 1 - autorizovaný, 2 - zakázaný',  created datetime Not Null   COMMENT '', PRIMARY KEY (id));
CREATE INDEX mail_pass ON user(mail, pass);
CREATE INDEX name ON user(name);
CREATE TABLE article( id integer Not Null  AUTO_INCREMENT COMMENT 'automaticky generované id',  title VarChar(250) Not Null   COMMENT 'Název článku (max 250 znaků)',  url VarChar (100) Not Null   COMMENT 'URL, pod kterým je článek dostupný',  content longtext Null   COMMENT 'Znění článku vč. HTML tagů a scriptů!',  last_edit datetime Not Null   COMMENT 'Datum poslední změny hodnoty',  last_editor integer Not Null   COMMENT 'ID uživatele, který naposledy článek editoval',  author integer Not Null   COMMENT 'ID uživatele, který článek vytvořil',  created datetime Not Null   COMMENT 'Datum založení článku', PRIMARY KEY (id), Constraint FK_user_author_id Foreign Key (author) References user(id), Constraint FK_user_last_editor_id Foreign Key (last_editor) References user(id));

